import "mapbox-gl/dist/mapbox-gl.css";
import Map, { Layer, Source, MapRef, Marker } from "react-map-gl";
import React, {useRef, useState } from "react";
import { cellToBoundary } from "h3-js";
import Papa from "papaparse";

const singaporeHexagonsObj = require('../data/singapore_hexagons.json');
const singaporeHexagonsArr = [];

function checkHexagons() {
  for (const hexagon in singaporeHexagonsObj) {
    console.log(hexagon);
  }
}


function MapBox() {

  const [arr, setArr] = useState([]);

  const onLoad = () => {
    // console.log("onLoad() called");
    getData();
    // checkHexagons();
  };


  async function getData() {
    const response = await fetch("./assets/singapore_hexagons.csv");

    // console.log(response);

    if (response) {
      const reader = response?.body?.getReader();
      const result = await reader?.read(); // raw array



      const decoder = new TextDecoder("utf-8");
      const csv = decoder.decode(result?.value); // the csv text
      const results = Papa.parse(csv, { header: true }); // object with { data, errors, meta }

      // console.log(results);

      const rows = results.data; // array of objects
      console.log(rows);
      const rs = rows.map((row) => {
        const style = getStyle(row);
        return {
          type: "Feature",
          properties: {
            color: style.color,
            opacity: style.opacity,
            id: row.hexindex7,
          },
          geometry: {
            type: "Polygon",
            coordinates: [cellToBoundary(row.hexindex7, true)],
          },
        };
      });
      setArr(rs);

      // console.log(rs);
    }
  }

  const getStyle = (row) => {
    const prediction = Math.floor(Math.random() * 10);

    if (Number(prediction) < 2) {
      return {
        color: '#FEDD87',
        opacity: 0.4,
      }
    }
    if (Number(prediction) < 5) {
      return {
        color: "#FC9653",
        opacity: 0.5,
      };
    }
    if (Number(prediction) < 7) {
      return {
        color: "#F77645",
        opacity: 0.6
      }
    }
    return {
      color: "#E14C48",
      opacity: 0.7
    };
  };

  // console.log(cellToBoundary(hexindex7Id, true));
  // console.log("arr:");
  // console.log(arr);



  return (

      <div>
        <div className="map">
          <Map
            initialViewState={{
              latitude: 1.290270,
              longitude: 103.851959,
              zoom: 10,
              bearing: 0,
              pitch: 0,
            }}
            mapStyle="mapbox://styles/mapbox/light-v9"
            mapboxAccessToken="pk.eyJ1IjoidGhlcHJvZiIsImEiOiJja3Q5amlqaXgxNjUwMm5wY3NrdmplbzVxIn0.C3zhU7lekidOJmARhNyBdw"
            style={{
              height: "100vh",
              width: "100vw",
            }}
            onLoad={onLoad}
          >
            <Source
              type="geojson"
              data={{
                type: "FeatureCollection",
                features: arr
              }}
            >
              <Layer
                {...{
                  id: "polyline-layer",
                  type: "fill",
                  paint: {
                    'fill-outline-color': 'white',
                    "fill-color": ["get", "color"],
                    "fill-opacity": ["get", "opacity"],
                  },
                }}
              />
            </Source>



          </Map>
        </div> 
      </div>
  );

};

export default MapBox;
