import "mapbox-gl/dist/mapbox-gl.css";
import Map, { Layer, Source, MapRef, Marker } from "react-map-gl";
import React, {useRef, useState } from "react";
import { cellToBoundary } from "h3-js";
import Papa from "papaparse";


function MapBox() {

  const hexindex7Id = "8744a1024ffffff";

  const onLoad = () => {
    console.log("onLoad() called");
    getData();
  };


  async function getData() {
    const response = await fetch("./assets/miami_hexagons.csv");

    console.log(response);

    if (response) {
      const reader = response?.body?.getReader();
      const result = await reader?.read(); // raw array
      const decoder = new TextDecoder("utf-8");
      const csv = decoder.decode(result?.value); // the csv text
      const results = Papa.parse(csv, { header: true }); // object with { data, errors, meta }
      const rows = results.data; // array of objects


      // console.log(rows);
      // const rs = rows.map((row: any) => {
      //   const color = getColor(row);
      //   return {
      //     type: "Feature",
      //     properties: {
      //       color,
      //       opacity: 0.8,
      //       id: row.hexindex7,
      //     },
      //     geometry: {
      //       type: "Polygon",
      //       coordinates: [cellToBoundary(row.hexindex7, true)],
      //     },
      //   };
      // });
      // setArr(rs);
    }
  }

  // console.log(cellToBoundary(hexindex7Id, true));



  return (

      <div>
        <div className="map">
          <Map
            initialViewState={{
              latitude: 25.917150273102553,
              longitude: -80.11504281788082,
              zoom: 10,
              bearing: 0,
              pitch: 0,
            }}
            mapStyle="mapbox://styles/mapbox/light-v9"
            mapboxAccessToken="pk.eyJ1IjoidGhlcHJvZiIsImEiOiJja3Q5amlqaXgxNjUwMm5wY3NrdmplbzVxIn0.C3zhU7lekidOJmARhNyBdw"
            style={{
              height: "100vh",
              width: "100vw",
            }}
            onLoad={onLoad}
          >
            <Source
              type="geojson"
              data= {{
                "type": "Feature",
                "geometry": {
                  "type": "Polygon",
                  "coordinates": [cellToBoundary(hexindex7Id, true)]
                },
                "id": "abc123"
              }}
            >
              <Layer
                {...{
                  id: "abc123",
                  type: "fill",
                  paint: {
                    'fill-outline-color': 'white',
                    "fill-color": "#E14C48",
                    "fill-opacity": 0.7,
                  },
                }}
              />

            </Source>



          </Map>
        </div> 
      </div>
  );

};

export default MapBox;
